import { collection, doc, docData } from 'rxfire/firestore';
import { map, switchMap, combineLatest } from 'rxjs/operators';

import { db } from './firebase.service';
import { Book } from 'models/book';

function getAllBooks() {
  return collection(db.collection('books').limit(12)).pipe(
   
    map(snapshot =>
      snapshot.map(doc => {
        console.log(doc.data());
        let books = new Book(doc.data());
        let channelId = books.channelId;
        let value0 = docData(db.collection('channels').doc(channelId)).pipe(val => {
          console.log(val)
          return val
        })
        return new Book(doc.data());
      })
    )
  );
}

export { getAllBooks };
