import { collection, doc, collectionChanges, fromCollectionRef, fromDocRef } from 'rxfire/firestore';
import { map, switchMap, combineLatest } from 'rxjs/operators';

import { db } from './firebase.service';
import { Book } from 'models/book';
import { Channel } from 'models/channel';
import { of } from 'rxjs';

class BookAndChannel {
  books: Book;
  nameChannel: string;
  constructor(data: any = {}) {
    this.books = data.books || null;
    this.nameChannel = data.nameChannel || '';
  }
}

function getAllBooks() {
  return collection(db.collection('books').limit(12)).pipe(
    map(snapshot =>
      snapshot.map(doc0 => {
        return new Book(doc0.data());
        // let books = doc0.data();
        // let channelId = books.channelId;
        // return collection(db.collection('channels').where('channelId', '==', channelId)).pipe(
        //   map(res => res.map(doc1 => {
        //     let resChennel = new Channel(doc1.data());
        //     let nameCN = resChennel.name;
        //     let rs = new BookAndChannel({ books: books, nameChannel: nameCN });
        //     return rs;
        //   }))
        // );
      })
    )
  );
}


function getAllBooks2() {
  //console.log('function 2')
  return collection(db.collection('books').limit(12)).pipe(
    switchMap(books => {
      const channelIds = books.map(v => v.data()['channelId']);
      console.log('log 1 ' + channelIds[0])
      return combineLatest(of(books), combineLatest(channelIds.map(channelId => {
        console.log('log 2 ' + channelId)
        doc(db.collection('channels').doc(channelId)).pipe(map(v => {
          console.log('log 3 ' + v);
          return v.data()['name'];
        }))
      })))
    }),

  );
}

export { getAllBooks, getAllBooks2 };
