import { collection, doc } from 'rxfire/firestore';
import { map, switchMap, combineLatest } from 'rxjs/operators';

import { db } from './firebase.service';
import { Book } from 'models/book';

class BookAndChannel {
  books: Book;
  nameChannel: string;
  constructor(data: any = {}) {
    this.books = data.books || null;
    this.nameChannel = data.nameChannel || '';
  }
}

function getAllBooks() {
  return collection(db.collection('books').limit(12)).pipe(
   
    map(snapshot =>
      snapshot.map(doc0 => {
        // console.log(doc0.data());
        let nameChannel;
        let books = doc0.data();
        let channelId = books.channelId;
        doc(db.collection('channels').doc(channelId)).subscribe(v => {
          // console.log(v.data());
          nameChannel = v.data().name;
        })
        //return new Book(doc0.data());
        let rs= new BookAndChannel({books, nameChannel});
        console.log(rs);
        return rs;
      })
    )
  );
}

export { getAllBooks };
