import { useObservable } from 'rxjs-hooks';
import React from 'react';
import Layout from 'components/global/layout';
import { getAllBooks } from 'services/book.service';
import './styles.scss';

export const Home = () => {
    const books = useObservable(() => getAllBooks(), []);
    return (
    <Layout title="Dashbord" description="This is the home page">
      <div className="container bt-line">
        <div className="row">
            <h2 className="title-oneach">Đề xuất</h2>
        </div>
        <div className="row">
        <div className="col-md-2 rs-pad mar-bot-25">
            <a>
              <div className="img-ct">
                <img src="https://i.ytimg.com/vi/qJtuxwle2XA/hqdefault.jpg?sqp=-oaymwEYCNIBEHZIVfKriqkDCwgBFQAAiEIYAXAB&rs=AOn4CLBGYFbC7oRAAz4oRy43quZWWuCc5Q" className="img-responsive" />
              </div>
              <div className="main-ct">
                <h3><a>Những Điều Cả Thế Giới Hiểu Lầm Về Krillin - Số Lần Tử Nạn Của Chàng Sư Cọ Khiến Fan Giật Mình</a></h3>
                <span>WW Anime</span>
                <div>
                  <span className="seen-count">127 N lượt xem</span>
                  <span>1 tuần trước</span>
                </div>
              </div>
            </a>
          </div>
        </div>
      </div>

      <p className="home-page">This is is the home page</p>

      {console.log('books', books)}
      {books.map((t, i) => {
        return (
          <div key={i}>
            <div>{t.name}</div>
            <div>{t.reader}</div>
          </div>
        );
      })}
    </Layout>
  )
}


export default Home;
