import { collection, doc, collectionChanges, fromCollectionRef, fromDocRef } from 'rxfire/firestore';
import { map, switchMap } from 'rxjs/operators';

import { db } from './firebase.service';
import { Book } from 'models/book';
import { Channel } from 'models/channel';
import { of, BehaviorSubject, combineLatest, from } from 'rxjs';

class BookAndChannel {
  books: Book;
  nameChannel: string;
  constructor(data: any = {}) {
    this.books = data.books || null;
    this.nameChannel = data.nameChannel || '';
  }
}

function getAllBooks() {
  let res= from (collection(db.collection('books').limit(12)).pipe(
    map(snapshot =>
      snapshot.map(doc0 => {
        let rs= new Book(doc0.data());
        return rs;
        // let channelId = rs.channelId;
        // return collection(db.collection('channels').where('channelId', '==', channelId)).pipe(
        //   map(res => res.map(doc1 => {
        //     let resChennel = new Channel(doc1.data()).name;
        //     return new BookAndChannel({ books: doc0.data(), nameChannel: resChennel });
        //   }))
        // );
      })
    )
  ));
  return res;
}

function GetChanelName(e){
  var subject = new BehaviorSubject<string>("");
  collection(db.collection('channels').where('channelId', '==', e)).pipe(
    map(res => res.map(doc1 => {
      let resChennel = new Channel(doc1.data());
      subject.next(resChennel.name);
    }))
  );
  return subject.value;
}


function getAllBooks2() {
  console.log('function 2')
  return collection(db.collection('books').limit(12)).pipe(
    switchMap(books => {
      const channelIds = books.map(v => v.data()['channelId']);
      console.log('log 1 ' + channelIds[0]);
      const books2 = books.map(v => v.data());
      return combineLatest(of(books2), combineLatest(channelIds.map(channelId => {
        console.log('log 2 ' + channelId)
        collection(db.collection('channels').where('channelId', '==', channelIds)).pipe(map(v => {
          console.log('log 3 ' + v);
          return v[0].data();
        }))
      })))
    }),
    map(([books, channels]) => {
      return books.map(book => {
        return {book, channels}
      })
      //return {books, channels};
    })
  );
}

export { getAllBooks, getAllBooks2 };
